import React,{useState} from 'react'

const Content = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [note, setNote] = useState({
        title:"",
        content:""
    });
    const [notes, setNotes] = useState([]);

    function handleTitle(event){
        setTitle(event.target.value);
    }

    function handleContent(event){
        setContent(event.target.value);
    }

    function handleClick(){
        setNote({title:title,content:content});
        setNotes(pre =>{
            return([...pre,note])
        })
    }

  return (
    <div>
    <div className="container mt-5 content">
        <div className='row'>
            <input type="text" onChange={handleTitle} name="title" placeholder='Title' value={title}></input>
        </div>
        <div className='row'>
            <textarea rows="5" onChange={handleContent} placeholder='Make a note....' value={content}></textarea>
            <span className='btn' onClick={handleClick}>Add</span> 
        </div>
    </div>

    {notes.map((note)=>{
        return(
        <div>
            <h3>{note.title}</h3>
            <p>{note.content}</p>
        </div>
        )
        })}
    </div>  
  )
}
export default Content;
